function getUserChannel(contactIdentity) {
    const CHANNEL = 1;
    const DEFAULT_CHANNEL = "other";
    const WHATSAPP_CHANNEL = "whatsapp";
    const BLIPCHAT_CHANNEL = "blipchat";
    const FACEBOOK_CHANNEL = "facebook";
    const INSTAGRAM_CHANNEL = "instagram"
    const WHATSAPP_IDENTIFIER = "wa.gw.msging.net";
    const BLIPCHAT_IDENTIFIER = "0mn.io";
    const FACEBOOK_IDENTIFIER = "messenger.gw.msging.net";
    const INSTAGRAM_IDENTIFIER = "instagram.gw.msging.net";
    const contactChannelIdentifier = contactIdentity.split("@")[CHANNEL];
    let userChannel = DEFAULT_CHANNEL;

    switch (contactChannelIdentifier) {
        case WHATSAPP_IDENTIFIER:
            userChannel = WHATSAPP_CHANNEL;
            break;
        case BLIPCHAT_IDENTIFIER:
            userChannel = BLIPCHAT_CHANNEL;
            break;

    }

    return userChannel;
}

function run(userChannel) {
    return getBoldTagsByChannel(userChannel)
}

function getBoldTagsByChannel(userChannel) {
    const DEFAULT_CHANNEL = "other";
    const WHATSAPP_CHANNEL = "whatsapp";
    const BLIPCHAT_CHANNEL = "blipchat";
    const INSTAGRAM_CHANNEL = "instagram"

    let channelBoldTags = {
        open: "<b>",
        close: "</b>",
        open_i: "<i>",
        close_i: "</i>"
    };

    switch (userChannel) {
        case WHATSAPP_CHANNEL:
        case INSTAGRAM_CHANNEL:
            channelBoldTags = {
                open: "*",
                close: "*",
                open_i: "_",
                close_i: "_"
            };
            break;
    }

    return channelBoldTags;
}

