    if (firstRegex.test(input))
        return { option: "1", content: "Itau" , codBanco:"341", nomeDoBanco:"BANCO ITAU S.A"}

    var secondRegex = new RegExp("((2)|(\s*2\s*)|(.*Banco do brasil.*)|(.*brasil.*))$", 'i');
    if (secondRegex.test(input))
        return { option: "2", content: "Banco do Brasil", codBanco:"1", nomeDoBanco:"BANCO DO BRASIL S.A." };

    var thirdRegex = new RegExp("((3)|(\s*3\s*)|(.*bradesco.*))$", 'i');
