        return { option: "3", content: "Bradesco", codBanco:"237", nomeDoBanco:"BANCO BRADESCO S.A." }

    var fourthRegex = new RegExp("((4)|(\s*4\s*)|(.*caixa econ[o|√¥]mica.*)|(.*caixa.*))$", 'i');
    if (fourthRegex.test(input))
        return { option: "4", content: "Caixa economica", codBanco:"104", nomeDoBanco:"CAIXA ECONOMICA FEDERAL" };

    var fithRegex = new RegExp("((5)|(\s*5\s*)|(.*Santander.*))$", 'i');
    if (fithRegex.test(input))
        return { option: "5", content: "Santander", codBanco:"33", nomeDoBanco:"BANCO SANTANDER" }

    var sixthRegex = new RegExp("((6)|(\s*6\s*)|(.*Safra.*))$", 'i');
    if (sixthRegex.test(input))
        return { option: "6", content: "Safra", codBanco:"422", nomeDoBanco:"BANCO SAFRA S/A" };

    var seventhRegex = new RegExp("((7)|(\s*7\s*)|(.*ver.*)|(.*mais.*)|(.*outros.*))$", 'i');
    if (seventhRegex.test(input))
        return { option: "7", content: "Ver mais bancos" };

    return { option: "invalid", content: "input inesperado" };
}

