function run(faturas) {
  faturas = JSON.parse(faturas).data.data.invoices;
  let retorno = { lista: [], mensagem: '', detalhes: [] };

  const monthNames = [
    'Jan',
    'Fev',
    'Mar',
    'Abr',
    'Mai',
    'Jun',
    'Jul',
    'Ago',
    'Set',
    'Out',
    'Nov',
    'Dez',
  ];

  const currencyFormatter = value => {
    return ('R$ ' + value.toFixed(2)).replace('.', ',');
  };

  for (i = 0; i < faturas.length; i++) {
    j = i + 1;

    const [year, month, day] = faturas[i].dueDate.split('-');
    let date = new Date(year, month - 1, day);

    if (faturas[i].statusDescription == 'Pago') {
      retorno.mensagem =
        retorno.mensagem +
        '*' +
        j +
        '-* ' +
        monthNames[date.getMonth()] +
        '/' +
        date.getFullYear() +
        ' (Paga)' +
        '\n';

      retorno.detalhes[j] = {
        vencimento:
          '*' +
          date.getUTCDate() +
          '/' +
          (date.getMonth() + 1) +
          '/' +
          date.getFullYear() +
          '*',
        valor: '*' + currencyFormatter(faturas[i].amount) + '*',
        status: '*Paga* ‚úÖ',
      };

      retorno.lista[j] = faturas[i];
    } else {
      retorno.mensagem =
        retorno.mensagem +
        '*' +
        j +
        '-* ' +
        monthNames[date.getMonth()] +
        '/' +
        date.getFullYear() +
        ' (Em aberto)' +
        '\n';

      retorno.detalhes[j] = {
        vencimento:
          '*' +
          date.getUTCDate() +
          '/' +
          (date.getMonth() + 1) +
          '/' +
          date.getFullYear() +
          '*',
        valor: '*' + currencyFormatter(faturas[i].amount) + '*',
        status: '*Em aberto* ‚è≥',
      };

      retorno.lista[j] = faturas[i];
    }
  }

  return retorno;
}

