function formatDate(date) {
    var meses = ['Jan', 'Fev', 'Mar', 'Abr', 'Mai', 'Jun', 'Jul', 'Ago', 'Set', 'Out', 'Nov', 'Dez'];
    var data = new Date(date);
    var mes = meses[data.getMonth()];
    var ano = data.getFullYear();
    return mes + '/' + ano;
}

