function run(numericOptionsRegex, options, input) {
  options = JSON.parse(options);

  if (options && options.length > 0) {
    numericOptionsRegex = JSON.parse(numericOptionsRegex);
    input = input.replace(/[\.\*\-\\\[\]{}_/"'!?~^;:,¬¥`=+&%$#()]/gm, '');

    if (input != '') {
      for (let i = 0; i < options.length; i++) {
        const numericOption = i + 1;
        const numericRegex = new RegExp(numericOptionsRegex[i], 'i');
        const contentRegex = new RegExp(options[i].regex, 'i');

        if (numericRegex.test(input) || contentRegex.test(input)) {
          return {
            option: numericOption.toString(),
            content: options[i].content,
          };
        }
      }
    }
  }

  return { option: 'invalid', content: 'input inesperado' };
}

